import { xf, print, } from '../functions.js';
import { isoDate, } from '../utils.js';
import { OAuthService, DialogMsg, stateParam, } from './enums.js';

function Intervals(args = {}) {
    const config = args.config;
    const api_uri = config.API_URI;
    const pwa_uri = config.PWA_URI;
    const intervals_client_id = config.INTERVALS_CLIENT_ID;
    const serviceName = OAuthService.intervals;
    let intervalsApiKey = '';
    

    xf.sub('ui:intervalsApiKey', x => {
        intervalsApiKey = x.intervalsApiKey
    });
    xf.sub('models:intervalsApiKeyRestore', x => {
        intervalsApiKey = x.intervalsApiKeyRestore
    });
    

    // Step D
    async function connect() {
        const scope = 'ACTIVITY:WRITE,CALENDAR:READ';
        const state = stateParam.encode(serviceName);
        stateParam.store(state);

        const url =
              'https://intervals.icu/oauth/authorize' +
              '?' +
              new URLSearchParams({
                  client_id: intervals_client_id,
                  redirect_uri: pwa_uri,
                  scope,
                  state,
              }).toString();
        window.location.replace(url);
    }

    async function disconnect() {
        // TODO:
        const url = "https://intervals.icu/oauth/deauthorize";
    }

    // Step 3
    async function paramsHandler(args = {}) {
        const state = args.state ?? '';
        const code = args.code ?? '';
        const scope = args.scope ?? '';

        const url = `${api_uri}/api/intervals/oauth/code` +
              '?' +
              new URLSearchParams({
                  state: state,
                  code: code,
                  scope: scope,
              })
              .toString();

        try {
            const response = await fetch(url, {
                method: 'POST',
                credentials: 'include',
            });

            const result = await response.text();
            print.log(result);
            clearParams();
        } catch (e) {
            console.log(``, e);
        }
    }

    function clearParams() {
        window.history.pushState({}, document.title, window.location.pathname);
    }

    async function uploadWorkout(blob) {
        const url = `${api_uri}/api/intervals/upload`;

        const formData = new FormData();
        formData.append('file', blob);

        try {
            const response = await fetch(url, {
                method: 'POST',
                credentials: 'include',
                body: formData,
            });

            if(response.ok) {
                return ':success';
            } else {
                if(response.status === 403) {
                    console.log(`:api :no-auth`);
                    xf.dispatch('action:auth', ':password:login');

                    xf.dispatch('ui:modal:error:open', DialogMsg.noAuth);
                }
                return ':fail';
            }
        } catch(error) {
            console.log(error);
            return ':fail';
        }
    }

    async function wod() {
        try {
            const event = await wodEvent();

            if(event !== ':fail') {
                const url = `https://intervals.icu/api/v1/athlete/0/events/${event.id}/downloadzwo`.toString();
                const response = await fetch(url, {
                    method: 'GET',
                    headers: new Headers({
                        'Authorization': 'Basic ' + btoa('API_KEY' + ":" + intervalsApiKey)
                    }),
                });
                if(response.ok) {
                    const text = await response.text();
                    console.log(text);
                    console.log(':success');
                    const body = [{
                        id: event.id,
                        start_date_local: event.name,
                        category: event.category,
                        name: event.name,
                        indoor: event.indoor,
                        workout_filename: `${event.name}.zwo`,
                        workout_file_base64: btoa(unescape(encodeURIComponent(text))) }
                    ];
                    return body;
                } else {
                    if(response.status === 403) {
                        console.log(`:api :no-auth`);
                        xf.dispatch('action:auth', ':password:login');
                        xf.dispatch('ui:modal:error:open', DialogMsg.noAuth);
                    }
                    console.log(':fail');
                }
            }
        } catch(error) {
            console.log(':error');
            console.error(error)
        }
    }

    async function wodEvent() {
        const oldest = isoDate();
        const newest = isoDate();

        const url = `https://intervals.icu/api/v1/athlete/0/events.json` +
              '?' +
              new URLSearchParams({
                  oldest,
                  newest,
                  limit: 1,
                  resolve: false
              })
              .toString();
              try {
              const response = await fetch(url, {
                method: 'GET',
                headers: new Headers({
                    'Authorization': 'Basic ' + btoa('API_KEY' + ":" + intervalsApiKey)
                }),
            });
            if(response.ok) {
                json = await response.json();
                return json[0];
                return ':success';
            } else {
                if(response.status === 403) {
                    console.log(`:api :no-auth`);
                    xf.dispatch('action:auth', ':password:login');
                    xf.dispatch('ui:modal:error:open', DialogMsg.noAuth);
                }
                return ':fail';
            }
        } catch(error) {
            console.log(error);
            return ':fail';
        }
    }

    async function wodMock() {

        const body = [{
            id: 47549572,
            start_date_local: `${isoDate()}T00:00:00`,
            category: "WORKOUT",
            name: "Intervals.icu Threshold",
            indoor: true,
            workout_filename: "Intervals_icu_Threshold.zwo",
            workout_file_base64: "" }
        ];

        return body;
    }

    return Object.freeze({
        connect,
        disconnect,
        paramsHandler,
        uploadWorkout,
        wod,

        wodMock,
    });
}

export default Intervals;

